"""
Tests population genetic statistic calculation.
"""

import unittest
import StringIO
import math
import dendropy
from dendropy.test.support import extendedtest
from dendropy.utility import messaging
from dendropy import popgenstat
_LOG = messaging.get_logger(__name__)

class TajimasDTests(extendedtest.ExtendedTestCase):

    def setUp(self):
        s = """\
            >s1
            ATAATAAAAA AATAATAAAA AAATAAAAAA AATAAAAAAA A
            >s2
            AAAAAAAATA AATAATAAAA AAATAAAAAA AAAAAAAAAA A
            >s3
            AAAATAAAAA TATAATAAAA AAATATAAAA AAAAAAAAAA A
            >s4
            AAAAAAAAAA AATAATAAAA AAATAAATAA ATAAAAAAAA A
            >s5
            AAAATAAAAA AAATATAAAA AAATAAAAAA AAAAAAAAAA A
            >s6
            AAAATAAAAA AAAAATAAAA AAAAAAAAAA AAAAATAAAA A
            >s7
            AAAAAATAAA AATAATAAAA AAATAAAAAA AAAAAAAAAA A
            >s8
            AAAAAAAAAA AAAAATAAAA AAATAAAAAA AAAAAAAAAT A
            >s9
            AAAAAAAAAA AAAAAAAAAA AAATAAAAAA AAAAAAAAAA A
            >s10
            AAAAAAAAAA AAAAATAAAA AAATAATAAA AAAAAAAAAA A"""
        self.matrix = dendropy.DnaCharacterMatrix.get_from_string(s, 'fasta')

    def testTajimasD(self):
        self.assertAlmostEqual(popgenstat.tajimas_d(self.matrix), -1.44617198561, 4)

class PopGenStatsTests(extendedtest.ExtendedTestCase):

    def testNucDifferences(self):

        s3 = StringIO.StringIO("""
    #NEXUS

    BEGIN TAXA;
        DIMENSIONS NTAX=12;
        TAXLABELS
            Lemur_catta
            Homo_sapiens
            Pan
            Gorilla
            Pongo
            Hylobates
            Macaca_fuscata
            Macaca_mulatta
            Macaca_fascicularis
            Macaca_sylvanus
            Saimiri_sciureus
            Tarsius_syrichta
        ;
    END;

    BEGIN CHARACTERS;
        DIMENSIONS NCHAR=474;
        FORMAT DATATYPE=DNA GAP=-;
        MATRIX
    Lemur_catta             
    Homo_sapiens            
    Pan                     
    Gorilla                 
    Pongo                   
    Hylobates               
    Macaca_fuscata          
    Macaca_mulatta          
    Macaca_fascicularis     
    Macaca_sylvanus         
    Saimiri_sciureus        
    Tarsius_syrichta        
        ;
    END;
    """)
        d = dendropy.DataSet(stream=s3, schema="nexus")
        self.assertAlmostEqual(popgenstat.average_number_of_pairwise_differences(d.char_matrices[0], ignore_uncertain=True),  111.0606, 4)
        self.assertAlmostEqual(popgenstat.nucleotide_diversity(d.char_matrices[0], ignore_uncertain=True), 0.2343, 4)

    def testPopulationPairSummaryStatistics(self):
        s3 = StringIO.StringIO("""
    #NEXUS
    Begin data;
        Dimensions ntax=23 nchar=747;
        Format datatype=dna;
        Matrix
    EPAC_AK1_E CGAAAAATATCCATCTCGCAGTGCTGAGCTAGACA    
    EPAC_AK2_F CGAAAAGTATCCATCTTGCAGTGCTGAGCTAGACA    
    EPAC_AK3_K CGCAAAGCACTTGCCCCATAGCGCTAAGCCGCGTT    
    EPAC_AK4_L CGCAAAGCGCTTGCCCCATAACGCTAAGCCGCGTT    
    EPAC_AK5_L CGCAAAGCGCTTGCCCCATAACGCTAAGCCGCGTT    
    EPAC_BC1_A CGAAAAGTATCCATCTCGCAGTGCTGAGCTAGACA    
    EPAC_BC2_I CGCGAAGCACTTGCCCCATAGCGCTAAGCCGCGTT    
    EPAC_BC3_J CGCGTAGCACTTGCCCCATAGCGCTAAGCCGCGTT    
    EPAC_BC4_K CGCAAAGCACTTGCCCCATAGCGCTAAGCCGCGTT    
    EPAC_BC5_K CGCAAAGCACTTGCCCCATAGCGCTAAGCCGCGTT    
    EPAC_BC6_L CGCAAAGCGCTTGCCCCATAACGCTAAGCCGCGTT    
    EPAC_BC7_L CGCAAAGCGCTTGCCCCATAACGCTAAGCCGCGTT    
    EPAC_BC8_L CGCAAAGCGCTTGCCCCATAACGCTAAGCCGCGTT    
    EPAC_BC9_L CGCAAAGCGCTTGCCCCATAACGCTAAGCCGCGTT    
    WPAC_JA1_A CGAAAAGTATCCATCTCGCAGTGCTGAGCTAGACA    
    WPAC_JA2_A CGAAAAGTATCCATCTCGCAGTGCTGAGCTAGACA    
    WPAC_JA3_A CGAAAAGTATCCATCTCGCAGTGCTGAGCTAGACA    
    WPAC_JA4_B CGAAAGGTATCCATCTCGCAGTGCTGAGTTAGACA    
    WPAC_JA5_C CAAAAAGTATCCATCTCGCAGTGCTGAGTTAGACA    
    WPAC_JA6_C CAAAAAGTATCCATCTCGCAGTGCTGAGTTAGACA    
    WPAC_JA7_D CGAAAAGTATCCATCTCGCAGTGCTGAACTAGACA    
    WPAC_JA8_G CGAAAAGTATCCATCTCGCAGTGCTAAGCTAGACA    
    WPAC_JA9_H TGAAAAGTATCCATCTCGCAGTGCTAAGCTAGACA    
        ;
    End;
    """)
        seqs = dendropy.DnaCharacterMatrix(stream=s3, schema="nexus")
        p1 = []
        p2 = []
        for idx, t in enumerate(seqs.taxon_set):
            if t.label.startswith('EPAC'):
                p1.append(seqs[t])
            else:
                p2.append(seqs[t])

        pp = popgenstat.PopulationPairSummaryStatistics(p1, p2)

        self.assertAlmostEqual(pp.average_number_of_pairwise_differences, 11.28063, 4)
        self.assertAlmostEqual(pp.average_number_of_pairwise_differences_between, 16.119047619, 4)
        self.assertAlmostEqual(pp.average_number_of_pairwise_differences_within, 10.2191697192, 4)
        self.assertAlmostEqual(pp.average_number_of_pairwise_differences_net, 5.89987789988, 4)
        self.assertEqual(pp.num_segregating_sites, 29)
        self.assertAlmostEqual(pp.wattersons_theta, 7.85734688643, 4)
        self.assertAlmostEqual(pp.tajimas_d, 1.65318627677, 4)
        self.assertAlmostEqual(pp.wakeleys_psi, 0.8034976, 2)

if __name__ == "__main__":
    unittest.main()

